import React from "react";
import { VStack, Image, Heading, Text, Box } from "@chakra-ui/react";

const getRandomColor = () => {
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const CoinCard = ({ name, img, price, symbol, currencySymbol, onClick }) => {
  const bgColor = getRandomColor();

  return (
    <Box
      as="button"
      w={"52"}
      h={"52"}
      bg={bgColor}
      shadow={"lg"}
      p={"8"}
      borderRadius={"full"}
      transition={"all 0.3s"}
      m={"4"}
      alignItems={"center"}
      display="flex"
      flexDirection="column"
      justifyContent="center"
      css={{
        "&:hover": {
          transform: "scale(1.1)",
          zIndex: 1, // Ensure card is on top during hover
        },
      }}
      onClick={onClick} // Handle click event
    >
      <Image
        src={img}
        w={"20"}
        h={"20"}
        objectFit={"contain"}
        alt={`${name} logo`}
      />
      <Heading size={"md"} noOfLines={1}>
        {currencySymbol}
        {price.toFixed(2)}
      </Heading>
      <Text noOfLines={1}>{name} ({symbol.toUpperCase()})</Text>
    </Box>
  );
};

export default CoinCard;
