import React from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Chart = ({ dataSets = [], currency, days }) => {
  // Map over the dataSets prop to generate labels and datasets
  const labels = dataSets.length > 0 
    ? dataSets[0].data.map(item => 
        days === "24h" 
          ? new Date(item[0]).toLocaleTimeString() 
          : new Date(item[0]).toLocaleDateString()
      ) 
    : [];

  const datasets = dataSets.map((dataSet, index) => ({
    label: dataSet.label || `Dataset ${index + 1}`,
    data: dataSet.data.map(item => item[1]),
    borderColor: dataSet.color || `hsl(${(index * 360) / dataSets.length}, 70%, 50%)`,
    backgroundColor: `rgba(${(index * 360) / dataSets.length}, 70%, 50%, 0.2)`,
    borderWidth: 2,
    pointRadius: 5,
    pointBackgroundColor: dataSet.color || `hsl(${(index * 360) / dataSets.length}, 70%, 50%)`,
    pointBorderColor: "#fff",
    pointHoverRadius: 7,
  }));

  const data = {
    labels: labels,
    datasets: datasets,
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allows the chart to take the full height of its container
    plugins: {
      legend: {
        display: true,
        position: 'top',
        labels: {
          color: '#333',
          font: {
            size: 14,
          },
        },
      },
      tooltip: {
        backgroundColor: '#fff',
        titleColor: '#333',
        bodyColor: '#333',
        borderColor: '#ddd',
        borderWidth: 1,
      },
      title: {
        display: true,
        text: `Price Trend (${currency})`,
        font: {
          size: 16,
          weight: 'bold',
        },
        color: '#333',
      },
    },
    scales: {
      x: {
        grid: {
          color: '#ddd',
          borderColor: '#ddd',
        },
        ticks: {
          color: '#333',
          font: {
            size: 12,
          },
        },
      },
      y: {
        grid: {
          color: '#ddd',
          borderColor: '#ddd',
        },
        ticks: {
          color: '#333',
          font: {
            size: 12,
          },
        },
      },
    },
  };

  return (
    <div style={{ width: '100%', height: '100vh' }}> {/* Full screen chart container */}
      <Line data={data} options={options} />
    </div>
  );
};

export default Chart;
