import React from "react";
import { Box, VStack, Image, Heading, Text } from "@chakra-ui/react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registering Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// Chart component
const Chart = ({ arr = [], currency, days }) => {
  const prices = [];
  const date = [];

  for (let i = 0; i < arr.length; i++) {
    if (days === "24h") date.push(new Date(arr[i][0]).toLocaleTimeString());
    else date.push(new Date(arr[i][0]).toLocaleDateString());
    prices.push(arr[i][1]);
  }

  const data = {
    labels: date,
    datasets: [
      {
        label: `Price in ${currency}`,
        data: prices,
        borderColor: "rgba(54, 162, 235, 1)",
        backgroundColor: "rgba(54, 162, 235, 0.2)",
        borderWidth: 4, // Increased to make the line bolder
        pointRadius: 5,
        pointBackgroundColor: "rgba(54, 162, 235, 1)",
        pointBorderColor: "#fff",
        pointHoverRadius: 7,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allows the chart to fill the container
    plugins: {
      legend: {
        display: true,
        position: 'top',
        labels: {
          color: '#333',
          font: {
            size: 14,
          },
        },
      },
      tooltip: {
        backgroundColor: '#fff',
        titleColor: '#333',
        bodyColor: '#333',
        borderColor: '#ddd',
        borderWidth: 1,
      },
      title: {
        display: true,
        text: `Price Trend (${currency})`,
        font: {
          size: 16,
          weight: 'bold',
        },
        color: '#333',
      },
    },
    scales: {
      x: {
        grid: {
          color: '#ddd',
          borderColor: '#ddd',
        },
        ticks: {
          color: '#333',
          font: {
            size: 12,
          },
        },
      },
      y: {
        grid: {
          color: '#ddd',
          borderColor: '#ddd',
        },
        ticks: {
          color: '#333',
          font: {
            size: 12,
          },
        },
      },
    },
  };

  return (
    <Box width="100%" height="100%" p={4} >
      <Line data={data} options={options} style={{ height: '100%', width: '100%' }} />
    </Box>
  );
};

// Main component
const CoinInfoPage = ({ coinData, chartData }) => {
  return (
    <VStack spacing={4} align="center" p={4} >
      {/* Coin Information */}
      <Box 
        w="100%" 
        maxW="600px" 
        p={4} 
        shadow="lg" 
        borderRadius="md" 
        bg="white"
        textAlign="center"
      >
        <Image
          src={coinData.img}
          alt={`${coinData.name} logo`}
          boxSize="80px"
          objectFit="contain"
          mx="auto"
        />
        <Heading size="lg" mt={4}>
          {coinData.name} ({coinData.symbol.toUpperCase()})
        </Heading>
        <Text fontSize="xl" mt={2}>
          {coinData.currencySymbol}
          {coinData.price.toFixed(2)}
        </Text>
      </Box>

      {/* Chart */}
      <Box width="100%" height="60vh" p={4} >
        <Chart arr={chartData.arr} currency={coinData.currency} days={coinData.days} />
      </Box>
    </VStack>
  );
};

export default CoinInfoPage;
