import { Heading, Image, Text, VStack, Box } from "@chakra-ui/react";
import React from "react";
import { Link } from "react-router-dom";

const CoinCard = ({ id, name, img, symbol, price, currencySymbol = "₹" }) => (
  <Link to={`/coin/${id}`}>
    <Box
      as="article"
      w={"52"}
      shadow={"lg"}
      p={"6"}
      borderRadius={"lg"}
      transition={"all 0.3s"}
      m={"4"}
      bg={"white"}
      _hover={{
        transform: "scale(1.05)",
        shadow: "xl",
        cursor: "pointer",
      }}
      display="flex"
      flexDirection="column"
      alignItems="center"
    >
      <Image
        src={img}
        w={"16"}
        h={"16"}
        objectFit={"contain"}
        alt={`${name} logo`}
        mb={4}
      />
      <Heading size={"md"} noOfLines={1} mb={2}>
        {symbol}
      </Heading>
      <Text noOfLines={1} fontWeight="bold" mb={2}>
        {name}
      </Text>
      <Text noOfLines={1}>
        {price ? `${currencySymbol}${price}` : "NA"}
      </Text>
    </Box>
  </Link>
);

export default CoinCard;
